export const LOADER_NAME = 'wext-manifest-loader';
export const ENVKeys = {
    DEV: 'dev',
    PROD: 'prod',
};
export const Browser = {
    CHROME: 'chrome',
    FIREFOX: 'firefox',
    OPERA: 'opera',
    EDGE: 'edge',
};
export const browserVendors = [
    Browser.CHROME,
    Browser.FIREFOX,
    Browser.OPERA,
    Browser.EDGE,
];
export const envVariables = [ENVKeys.DEV, ENVKeys.PROD];
export const CUSTOM_PREFIX_REGEX = new RegExp(`^__((?:(?:${[...browserVendors, ...envVariables].join('|')})\\|?)+)__(.*)`);
